import React from "react";
import Navbar from "./Navbar";
import MyButton from "./MyButton";
import Footer from "./Footer";
import { motion } from "framer-motion"; // ðŸ‘‰ import

function App() {
  const handleClick = (msg: string) => {
    alert(msg);
  };

  return (
    <div className="font-sans">
      <Navbar />

      {/* Hero Section */}
      <motion.div
        id="home"
        initial={{ opacity: 0, y: 50 }}        // ðŸ‘‰ Starting state
        animate={{ opacity: 1, y: 0 }}         // ðŸ‘‰ Animation end state
        transition={{ duration: 1, ease: "easeOut" }} // ðŸ‘‰ Speed/smoothness
        className="flex flex-col justify-center items-center text-center text-white min-h-[100vh] bg-cover bg-center px-6 py-20"
        style={{
          backgroundImage:
            "url('https://t4.ftcdn.net/jpg/01/85/58/17/360_F_185581700_CNwpjWkJjYUAzIvsg82fYvMMcIB27Se9.jpg')",
        }}
      >
        <h1 className="text-[4rem] md:text-[6rem] font-bold">
          <span className="text-orange-500">Welcome to </span>
          <span className="text-white">TailorCraft</span>
        </h1>
        <p className="text-lg md:text-xl mt-4">
          Custom Suits, Dresses, Alterations & More{" "}
          <strong>"Where every stitch tells a story of elegance."</strong>
        </p>

        <div className="mt-6">
          <MyButton
            title="See Our Work"
            onClick={() => handleClick("Hello from React!")}
          />
        </div>
      </motion.div>

      {/* About Section */}
      <motion.section
        id="about"
        initial={{ opacity: 0, x: -100 }}
        whileInView={{ opacity: 1, x: 0 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
        className="bg-stone-500 min-h-screen flex flex-col justify-center items-center text-center px-6 py-16"
      >
        <h2 className="text-5xl font-bold mb-6 text-white">About</h2>
        <p className="text-lg mb-4 max-w-2xl text-white">
          Welcome to Jennels Tailoring, where 10 years of experience meets craftsmanship and style...
        </p>
      </motion.section>

      <Footer />
    </div>
  );
}

export default App;
